properties.property:
logger.info("Started Loader");
var host = globalMap.get('host');
var port = globalMap.get('port');
var database = globalMap.get('database');
var username = globalMap.get('username');
var password = globalMap.get('password');

var customer_file_path = globalMap.get('customer_file_path');
var location_file_path = globalMap.get('location_file_path');
var locationrole_file_path = globalMap.get('locationrole_file_path');
var device_file_path = globalMap.get('device_file_path');

//logger.info("username" + username)
logger.info("Loading customer from : " + customer_file_path);
var obj = new Packages.com.covidien.csvreader.reader.CustomerReader(host, database, username, password, port);
obj.readCustomerCSVFile(customer_file_path);
logger.info("Completed customer loading");

logger.info("Loading Location from : " + location_file_path);
var obj1 = new Packages.com.covidien.csvreader.reader.LocationReader(host, database, username, password, port);
obj1.readLocationCSVFile(location_file_path);
logger.info("Completed Location loading");

logger.info("Loading LocationRole from : " + locationrole_file_path);
var obj1 = new Packages.com.covidien.csvreader.reader.LocationRoleReader(host, database, username, password, port);
obj1.readLocationRoleCSVFile(locationrole_file_path);
logger.info("Completed LocationRole loading");

logger.info("Loading Device from : " + device_file_path);
var obj1 = new Packages.com.covidien.csvreader.reader.DeviceReader(host, database, username, password, port);
obj1.readDeviceCSVFile(device_file_path);
logger.info("Completed Device loading");

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;

channel.shutdownScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;
